import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  function handleSubmit(e) {
    e.preventDefault();

    // Simple fake validation
    if (!email || !password) {
      alert('Please enter email and password');
      return;
    }

    // Fake login success: save user to localStorage
    localStorage.setItem('user', email);

    // Redirect to main app page
    navigate('/');
  }

  return (
    <div style={{ maxWidth: 400, margin: '100px auto', padding: 20, border: '1px solid #ccc', borderRadius: 6 }}>
      <h2 style={{ textAlign: 'center' }}>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>Email:</label><br />
        <input
          type="email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          placeholder="email@example.com"
          style={{ width: '100%', padding: 8, marginBottom: 10 }}
          required
        />
        <br />
        <label>Password:</label><br />
        <input
          type="password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          placeholder="Password"
          style={{ width: '100%', padding: 8, marginBottom: 20 }}
          required
        />
        <br />
        <button type="submit" style={{ width: '100%', padding: 10, backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: 4 }}>
          Log In
        </button>
      </form>
    </div>
  );
}
