import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const [submissions, setSubmissions] = useState([]);
  const [loadingUsers, setLoadingUsers] = useState(true);
  const [loadingSubs, setLoadingSubs] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/users')
      .then(res => {
        setUsers(res.data);
        setLoadingUsers(false);
      })
      .catch(() => {
        setError('Failed to load users.');
        setLoadingUsers(false);
      });

    axios.get('http://localhost:5000/api/submissions')
      .then(res => {
        setSubmissions(res.data);
        setLoadingSubs(false);
      })
      .catch(() => {
        setError('Failed to load submissions.');
        setLoadingSubs(false);
      });
  }, []);

  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div style={{ maxWidth: 900, margin: '20px auto', fontFamily: 'Arial' }}>
      <h2>Admin Dashboard</h2>

      <section style={{ marginBottom: 40 }}>
        <h3>Logged In Users</h3>
        {loadingUsers ? <p>Loading users...</p> : (
          <table border="1" cellPadding="8" style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th>Email</th>
                <th>Last Login</th>
              </tr>
            </thead>
            <tbody>
              {users.map(u => (
                <tr key={u.email}>
                  <td>{u.email}</td>
                  <td>{new Date(u.lastLogin).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      <section>
        <h3>Test Submissions</h3>
        {loadingSubs ? <p>Loading submissions...</p> : (
          <table border="1" cellPadding="8" style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th>User Email</th>
                <th>Submission Time</th>
                <th>Answers Summary</th>
              </tr>
            </thead>
            <tbody>
              {submissions.map((sub, i) => (
                <tr key={i}>
                  <td>{sub.user}</td>
                  <td>{new Date(sub.submittedAt).toLocaleString()}</td>
                  <td>
                    {Object.entries(sub.answers).map(([qId, ans]) => (
                      <div key={qId}>
                        Q{qId}: {ans}
                      </div>
                    ))}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
}
